\n}\n\n.background_dark {\n    background-color: $dark_background;\n}\n\n.background_dark {\n    background-color: $dark_background;\n}\n","// fonts\n$primary_font: \"Source Sans Pro\", sans-serif;\n\n// Colors\n$black: #161518;\n$white: #fff;\n$dark_grey: #3a3a3a;\n$light_grey: #f7f7f7;\n$text_grey: #545258;\n$text_light_grey: #898590;\n$border_grey: #b1b1b1;\n$primary_color: #f24b4b;\n$light_background: #2a2237;\n$dark_background: #140b1f;\n$footer_background: #f9f9f9;\n$scroll_grey: #5c5c5c;\n$scroll_grey_bg: #b3b3b3;\n$link_color: #6B39CD;\n\n// Font sizes\n$h1: 48px;\n$h2: 36px;\n$h3: 24px;\n$h4: 18px;\n$h5: 12px;\n$h1_mobile: 30px;\n$h2_mobile: 24px;\n$h3_mobile: 18px;\n$h4_mobile: 16px;\n$h5_mobile: 12px;\n\n// Text\n$text_size: 16px;\n$small_text_size: 10px;\n$text_size_mobile: 14px;\n$small_text_size_mobile: 10px;\n\n// Menu\n$menu_text: 14px;\n$menu_text_mobile: 28px;\n\n// Buttons\n$button_text: 18px;\n$button_text_mobile: 18px;\n\n// Placeholder\n$placeholder: 12px;\n$placeholder_mobile: 10px;\n\n// Font Weights\n$light: 300;\n$regular: 400;\n$semibold: 600;\n$bold: 700;\n$blackbold: 900;\n\n// Line heights\n$h1_line: 68px;\n$h2_line: 56px;\n$h3_line: 44px;\n$h4_line: 38px;\n$h5_line: 22px;\n$h1_line_mobile: 46px;\n$h2_line_mobile: 46px;\n$h3_line_mobile: 38px;\n$h4_line_mobile: 38px;\n$h5_line_mobile: 22px;\n$text_size_line: 34px;\n$small_text_size_line: 20px;\n$text_size_mobile_line: 30px;\n$small_text_size_mobile_line: 20px;\n\n// responsive\n$mobile: 420px;\n$tablet: 768px;\n$desktop: 992px;\n\n// Transition\n$transition_time: .3s;\n\n// Box shadows\n$box_shadow: 0 5px 10px rgba(0,0,0,.20);\n","html,\nbody {\n    font-family: $primary_font;\n    -webkit-font-smoothing: antialiased;\n    font-style: normal;\n\n    color: $text_grey;\n\n    @include text;\n}\n\nh1,\nh2,\nh3,\nh4,\nh5 {\n    color: $black;\n}\n\nh1 {\n    @include h1;\n}\n\nh2 {\n    @include h2;\n}\n\nh3 {\n    @include h3;\n}\n\nh4 {\n    @include h4;\n}\n\nh5 {\n    @include h5;\n}\n\na {\n    color: $link_color;\n    transition: $transition_time;\n    text-decoration: none;\n    font-weight: $bold;\n    &:active,\n    &:visited {\n        color: darken($link_color, 10%);\n    }\n    &:hover {\n        color: darken($link_color, 20%);\n    }\n}\n\n.text_small {\n    @include small_text;\n}\n\n.text_bold {\n    font-weight: $bold;\n}\n\n.text_medium {\n    font-weight: $regular;\n}\n\n.text_regular {\n    font-weight: $regular;\n}\n\n.text_primary {\n    color: $primary_color;\n}\n\n.text_black {\n    color: $black;\n}\n\n.text_white {\n    color: $white;\n}\n\n.text_right {\n    text-align: right;\n}\n\n.text_center {\n    text-align: center;\n}\n\n.text_left {\n    text-align: left;\n}\n","@mixin h1() {\n    font-size: $h1;\n    font-weight: $bold;\n    line-height: $h1_line;\n    @media only screen and(max-width: $tablet) {\n        font-size: $h1_mobile;\n        line-height: $h1_line_mobile;\n    }\n}\n\n@mixin h2() {\n    font-size: $h2;\n    font-weight: $semibold;\n    line-height: $h2_line;\n    @media only screen and(max-width: $tablet) {\n        font-size: $h2_mobile;\n        line-height: $h2_line_mobile;\n    }\n}\n\n@mixin h3() {\n    font-size: $h3;\n    font-weight: $semibold;\n    line-height: $h3_line;\n    @media only screen and(max-width: $tablet) {\n        font-size: $h3_mobile;\n        line-height: $h3_line_mobile;\n    }\n}\n\n@mixin h4() {\n    font-size: $h4;\n    font-weight: $semibold;\n    line-height: $h4_line;\n    @media only screen and(max-width: $tablet) {\n        font-size: $h4_mobile;\n        line-height: $h4_line_mobile;\n    }\n}\n\n@mixin h5() {\n    font-size: $h5;\n    font-weight: $semibold;\n    line-height: $h5_line;\n    @media only screen and(max-width: $tablet) {\n        font-size: $h5_mobile;\n        line-height: $h5_line_mobile;\n    }\n}\n\n@mixin text() {\n    font-size: $text_size;\n    font-weight: $regular;\n    line-height: $text_size_line;\n\n\n    @media only screen and(max-width: $tablet) {\n        font-size: $text_size_mobile;\n        line-height: $text_size_mobile_line;\n    }\n}\n\n@mixin small_text() {\n    font-size: $small_text_size;\n    font-weight: $regular;\n    line-height: $small_text_size_line;\n\n\n    @media only screen and(max-width: $tablet) {\n        font-size: $small_text_size_mobile;\n        line-height: $small_text_size_mobile_line;\n    }\n}\n\n\n@mixin link_text() {\n    font-size: $small_text_size;\n    font-weight: $bold;\n    line-height: $small_text_size_line;\n\n    text-decoration: none;\n    letter-spacing: 1px;\n    text-transform: uppercase;\n\n    color: $black;\n    @media only screen and(max-width: $tablet) {\n        font-size: $small_text_size_mobile;\n        line-height: $small_text_size_mobile_line;\n    }\n}\n\n@mixin truncate() {\n    overflow: hidden;\n\n    white-space: nowrap;\n    text-overflow: ellipsis;\n}\n\n@mixin cover($url) {\n    background-image: url($url);\n    background-size: cover;\n    background-position: center;\n    background-repeat: no-repeat;\n}",".padding_small {\n    padding-top: 60px;\n    padding-bottom: 60px;\n    @media only screen and(max-width: $tablet) {\n        padding-top: 30px;\n        padding-bottom: 30px;\n    }\n}\n\n.padding_medium {\n    padding-top: 90px;\n    padding-bottom: 90px;\n    @media only screen and(max-width: $tablet) {\n        padding-top: 60px;\n        padding-bottom: 60px;\n    }\n}\n\n.padding_big {\n    padding-top: 120px;\n    padding-bottom: 120px;\n    @media only screen and(max-width: $tablet) {\n        padding-top: 90px;\n        padding-bottom: 90px;\n    }\n}\n",".contact-leave-to,\n.contact-enter {\n\ttransform: translateY(-45px);\n\ttransition: $transition_time;\n}\n\n.contact-leave-ative,\n.contact-enter-active {\n\ttransition: $transition_time;\n}\n\n.contact-leave,\n.contact-enter-to {\n\ttransform: translateY(0);\n\ttransition: $transition_time;\n}\n\n.navigation-leave-to,\n.navigation-enter {\n\ttransform: translateX(100%);