max-width: $tablet)\n      {\n        display: none;\n      }\n    }\n    span\n    {\n      font-size: 13px;\n\n      width: 140px;\n\n      cursor: pointer;\n      text-align: center;\n\n      color: $white;\n      img\n      {\n        position: absolute;\n        left: 50%;\n\n        width: 13px;\n\n        transform: translate(-50%);\n      }\n      &:nth-of-type(1)\n      {\n        position: absolute;\n        top: -80px;\n        img\n        {\n          top: 30px;\n        }\n        @media only screen and(max-width: $tablet)\n        {\n          top: -130px;\n        }\n      }\n      &:nth-of-type(2)\n      {\n        position: absolute;\n        top: 60px;\n\n        transition: .3s;\n\n        opacity: .4;\n        img\n        {\n          top: -20px;\n        }\n        @media only screen and(max-width: $tablet)\n        {\n          top: 70px;\n        }\n      }\n    }\n  }\n}\n\n.scroll-small\n{\n  font-size: 10px;\n  font-weight: $bold;\n  line-height: 11px;\n\n  position: fixed;\n  z-index: 3;\n  right: 0;\n  bottom: 55px;\n\n  transition: .3s;\n  transform: rotate(-90deg);\n  transform-origin: 50% 50%;\n  animation: bounce 2s ease 3s infinite;\n  letter-spacing: 2px;\n  text-transform: uppercase;\n\n  opacity: 1;\n  color: $white;\n\n  mix-blend-mode: difference;\n  will-change: opacity transform;\n  @media only screen and(max-width: 768px)\n  {\n    right: -20px;\n  }\n  &.hide-scroll\n  {\n    bottom: -100px;\n  }\n  img\n  {\n    margin-right: 5px;\n\n    vertical-align: bottom;\n  }\n}\n\n.back_link\n{\n  position: absolute;\n  top: 100vh;\n  top: calc(100vh - 45px);\n  left: 30px;\n\n  transition: .3s ease-in-out;\n\n  color: $black;\n\n  @include link_text;\n  img\n  {\n    height: 11px;\n\n    vertical-align: middle;\n  }\n  &.hidden-link\n  {\n    left: -100px;\n  }\n  &:active,\n  &:visited,\n  &:hover\n  {\n    color: $black;\n  }\n  @media only screen and(max-width: 768px)\n  {\n    display: none;\n  }\n  @media only screen and(min-width: 1440px)\n  {\n    position: fixed;\n  }\n}\n\n@keyframes bounce\n{\n  from,\n  20%,\n  53%,\n  80%,\n  to\n  {\n    -webkit-transform: rotate(-90deg) translate3d(0,0,0);\n            transform: rotate(-90deg) translate3d(0,0,0);\n    -webkit-animation-timing-function: cubic-bezier(.215, .610, .355, 1.000);\n            animation-timing-function: cubic-bezier(.215, .610, .355, 1.000);\n  }\n\n  40%,\n  43%\n  {\n    -webkit-transform: rotate(-90deg) translate3d(15px, 0, 0);\n            transform: rotate(-90deg) translate3d(15px, 0, 0);\n    -webkit-animation-timing-function: cubic-bezier(.755, .050, .855, .060);\n            animation-timing-function: cubic-bezier(.755, .050, .855, .060);\n  }\n\n  70%\n  {\n    -webkit-transform: rotate(-90deg) translate3d(7px, 0, 0);\n            transform: rotate(-90deg) translate3d(7px, 0, 0);\n    -webkit-animation-timing-function: cubic-bezier(.755, .050, .855, .060);\n            animation-timing-function: cubic-bezier(.755, .050, .855, .060);\n  }\n\n  90%\n  {\n    -webkit-transform: rotate(-90deg) translate3d(3px, 0, 0);\n            transform: rotate(-90deg) translate3d(3px, 0, 0);\n  }\n}\n\nli.link\n{\n  line-height: 14px;\n\n  padding: 0 10px;\n  &.animate\n  {\n    color: red;\n    span\n    {\n      animation: link .6s forwards;\n    }\n  }\n  a\n  {\n    line-height: 14px!important;\n\n    position: relative;\n\n    display: inline-block;\n    overflow: hidden;\n\n    padding: 0 5px;\n\n    color: #fff;\n    &.active\n    {\n      span\n      {\n        left: 0;\n\n        transition: .3s;\n        animation: none;\n      }\n    }\n    span\n    {\n      position: absolute;\n      top: 50%;\n      left: -105%;\n\n      width: 100%;\n      height: 2px;\n\n      transform: translateY(-50%);\n      transform-origin: left center;\n\n      background-color: $black;\n    }\n  }\n}\n\n@keyframes link\n{\n  from\n  {\n    left: -105%;\n  }\n  to\n  {\n    left: 105%;\n  }\n}\n\n.page-enter-active,\n.page-leave-activ